module.exports = function(data) {
	const globals = data.globals;
	const baseUrl = globals.directus_base_url;
	const inputs = data.content_workflow.inputs

	// There's three types of inputs: file (audio/video), text, and image
	// Let's loop through all the inputs and format them for OPENAI API chat/completions
	const messageContent = inputs.map(input => {
		if (input.input_type === 'file') {
			if(!input.file_transcript_text) {
				throw new Error('No file transcript text found')
			}
			return {
				type: 'text',
				text: input.input_name + '//' + input.file_transcript_text
			}
		} else if (input.input_type === 'text') {
			return {
				type: 'text',
				text:  input.input_name + '//' + input.text
			}
		} else if (input.input_type === 'image') {
			return {
				type: 'image_url',
				image_url: {
					url: `${baseUrl}/assets/${input.image}`
				}
			}
		}
	})

	messageContent.unshift({
		type: 'text',
		text: 'Use the following notes, files, or images to help you generate a response:'
	})

	const messageThread = [{
		"role": "user",
		"content": messageContent
	}]

	return {
		messageThread
	}
}

function slugify(text) {
    // Normalize Unicode characters to ASCII
    text = text.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

    // Convert to lowercase
    text = text.toLowerCase();

    // Replace spaces and other non-alphanumeric characters with hyphens
    text = text.replace(/\s+/g, '-'); // Replace spaces with hyphens
    text = text.replace(/[^a-z0-9-]/g, '-'); // Replace other non-alphanumeric characters with hyphens

    // Remove duplicate hyphens
    text = text.replace(/-+/g, '-');

    // Trim leading and trailing hyphens
    text = text.replace(/^-+|-+$/g, '');

    return text;
}

module.exports = async function(data) {
	return slugify(data.$trigger.text)
}

module.exports = function(data) {
	const workflow = data.content_workflow

    const payload = {
        title: workflow.post_title,
        slug: workflow.post_slug,
        image: workflow.post_image,
        date_published: data.$trigger.body.date_published,
        content_workflow: data.$trigger.body.keys[0],
    }

    payload.translations = [
        {
            title: workflow.post_title,
	        slug: workflow.post_slug,
            content: workflow.post_content,
            languages_code: 'en-US'
        }
    ]

    return {
        post: payload,
        now: new Date().toISOString(),
    }
}

function triggerKeys(trigger) {
  // Normalize inputs to always return an array of keys to make it easier to work with
  if (trigger.event.includes('.create') && trigger.key && typeof trigger.key === 'string') {
      return [trigger.key]
  }

  else if (trigger.event.includes('.update') && trigger.keys && Array.isArray(trigger.keys)) {
      return trigger.keys
  }
}

module.exports = function(data) {
  const keys = triggerKeys(data.$trigger)
  const baseUrl = data.globals.directus_base_url

  // We only want to trigger the transcription if the following is true:
  // The input_type === 'file'
  // file_url or file exist
  // If both file_url and file exist, we will prioritize file
  const payload = data.$trigger.payload

  if (payload.input_type !== 'file') {
    throw new Error('Stoping execution because the input_type is not a file.')
  }

  if (!payload.file && !payload.file_url) {
    throw new Error('Stopping execution because either file or file_url is required.')
  }

  let fileId

  if (payload.file) {
    // If file is a string, we'll assume it's the file id
    if (typeof payload.file === 'string') {
      fileId = payload.file
    }

    // If the file is an object, we'll assume it's the file object and we'll extract the id
    if (typeof payload.file === 'object') {
      fileId = payload.file.id
    }
  }

  let url = fileId ? `${baseUrl}/assets/${fileId}` : payload.file_url

  return {
    keys,
    file_url: url,
  }
}

