function combineObjects(array) {
  return array.reduce((acc, obj) => {
    return { ...acc, ...obj };
  }, {});
}

module.exports = function(data) {
  const translations = data.translations;
  const { postId, translationId } = data.format_loop;
  const language_to = data.$trigger.body.language_to.key; // de

  const translationPayload = combineObjects(translations)

  const payload = {
    posts_id: postId,
    languages_code: language_to,
    id: translationId ?? null,
    ...translationPayload,
  }

  return payload;
}

module.exports = function (data) {
  try {
    const postList = JSON.parse(data.generate_social_posts);

    if (!Array.isArray(postList.posts)) {
      throw new Error(
        "Response from OpenAI is missingposts or is not an array"
      );
    }

    const addToEachPost = {};

    addToEachPost.template = data.$trigger.body.template.key;
    addToEachPost.content_workflow = data.$trigger.body.keys[0];

    const posts = postList.posts.map((post) => {
      return {
        ...addToEachPost,
	    channel: post.channel,
        content: post.content,
        rating: post.rating,
      };
    });

    return posts;
  } catch (error) {
    throw new Error(error.message);
  }
};

