function getTranslation(post, langCode) {
    if (post.translations) {
        return post.translations.find(translation => translation.languages_code === langCode);
    }
    return null;
}

function checkFields(translation, fieldsToTranslate) {
    if (translation) {
        return fieldsToTranslate.every(field => translation[field]);
    }
    return false;
}

module.exports = function(data) {
	const language_from = data.$trigger.body.language_from.key; // en-US
	const language_to = data.$trigger.body.language_to.key; // de
	const fieldsToTranslate = data.$trigger.body.fields;
	// [
    //     "title",
    //     "content",
    //     "slug"
    //   ]

	const post = data.get_post;

    // Check if the post has a translation for the language_from
    const fromTranslation = getTranslation(post, language_from);

    if(!fromTranslation) {
        throw new Error(`Translation for ${language_from} not found`);
    }

    if(!checkFields(fromTranslation, fieldsToTranslate)) {
        throw new Error(`Fields to translate not found in ${language_from} translation`);
    }

    // Check if the post already has a translation for the language_to
    const toTranslation = getTranslation(post, language_to);

	// Loop through fields and format the data as an array of objects to send to the DeepL API via the Util: Translate Content flow.
    // [
    //     {
    //         field: 'title',
    //         text: fromTranslation.title,
    //         language_from: language_from,
    //         language_to: language_to
    //     }
    // ]

    const translationData = fieldsToTranslate.map(field => {
        return {
            field,
            value: fromTranslation[field],
			language_to: language_to.split('-')[0].toUpperCase(),
        }
    });

    return {
        translationData,
        postId: post.id,
        translationId: toTranslation ? toTranslation.id : null
    };
}

